

def have_unique_digits(code : str):
    return len(set(code)) == len(code)

def characters_to_be_processed_before_N_different(data_buffer:str, N:int):

    for i in range(0, len(data_buffer)-N):
        if have_unique_digits(data_buffer[i:i+N]):
            return i+N


if __name__ == "__main__":
    data_buffer = ""
    print(characters_to_be_processed_before_N_different(data_buffer, 4))
    print(characters_to_be_processed_before_N_different(data_buffer, 14))


